t=['input[type=text]','textarea'];function w(m,n){let i;if(n.data)i=n.data;else{if("insertFromPaste"!=n.inputType)return;i=m.value}l=i.length,v=m.value,s=m.selectionStart-l,e=m.selectionEnd,o=v.substring(0,s);for(j=0;j<l;j++)o+=(s+j)%2==1?i[j].toUpperCase():i[j].toLowerCase();m.value=o+v.substring(e),m.setSelectionRange(s+l,s+l)}window.onload=()=>document.querySelectorAll(t.join(",")).forEach(m=>m.addEventListener("input",n=>w(m,n)));